function [day,focus] = find_day_focus(file_path)    % day和focus计算    flag_right = 0;  % 找右括号    flag_left = 0;  % 找左括号    flag_space = 0; % 找 day 和 focus 之间的空格    day = "";   % 日期    focus = ""; % focus    for i = length(file_path) : -1 : 1        if file_path(i) == ')' && flag_right == 0            flag_right = i;        end        if file_path(i) == ' ' && flag_space == 0            flag_space = i;            if flag_right > flag_space                focus(1, :) = file_path(flag_space + 1 : flag_right - 1);            else                error('没找到右括号')            end        end        if file_path(i) == '(' && flag_left == 0            flag_left = i;            if flag_space > flag_left                day(1, :) = file_path(flag_left + 1 : flag_space - 1);            else                error('没找到空格')            end        end     endend